fn main() -> void {
    var text: string = "Hello, World!";
    print("String:", text);
    print("Length:", text.length());

    var concatenated: string = text + " How are you?";
    print("Concatenated:", concatenated);
}

fn main() -> void {
    var ch: char = 'H';
    print("Character:", ch);

    var unicode_char: char = '\u0041'; // 'A' in Unicode
    print("Unicode char:", unicode_char);
}

fn main() -> void {
    var size: usize = 42;
    var array: i32[size]; // Using usize for array sizing
    print("Array size:", size);
}

fn main() -> void {
    var signed_size: isize = -10;
    var positive_size: isize = 20;
    print("Signed size:", signed_size);
    print("Positive size:", positive_size);
}

fn main() -> void {
    fn no_return() -> void {
        print("This function returns nothing");
    }

    no_return();
}

fn main() -> void {
    var flag: bool = true;
    var condition: bool = false;

    print("Flag:", flag);
    print("Condition:", condition);

    if (flag && !condition) {
        print("Logic works!");
    }
}

