fn main() -> void {
    var ptr: *i32 = null;

    if (ptr == null) {
        print("Pointer is null");
    }

    ptr = std::Legacy::new<i32>(42);
    if (ptr != null) {
        print("Pointer is not null, value:", *ptr);
    }
}

fn main() -> void {
    var numbers: vec<i32> = {1, 2, 3, 4, 5};
    numbers.push_back(6);

    print("Vector size:", numbers.size());
    for num in numbers {
        print("Number:", num);
    }
}

fn main() -> void {
    var scores: map<string, i32> = map<string, i32>();
    scores["Alice"] = 95;
    scores["Bob"] = 87;
    scores["Charlie"] = 92;

    print("Alice's score:", scores["Alice"]);

    for (key, value) in scores {
        print(key, ":", value);
    }
}

fn main() -> void {
    var fixed_array: array<i32, 5> = {1, 2, 3, 4, 5};

    print("Array size:", fixed_array.size());
    for i in std::range(fixed_array.size()) {
        print("Element", i, ":", fixed_array[i]);
    }
}

fn main() -> void {
    var linked_list: list<i32> = list<i32>();
    linked_list.push_back(1);
    linked_list.push_back(2);
    linked_list.push_front(0);

    print("List size:", linked_list.size());
    for item in linked_list {
        print("Item:", item);
    }
}

fn main() -> void {
    var unique_numbers: set<i32> = set<i32>();
    unique_numbers.insert(3);
    unique_numbers.insert(1);
    unique_numbers.insert(4);
    unique_numbers.insert(1); // Duplicate, won't be added

    print("Set size:", unique_numbers.size());
    for num in unique_numbers {
        print("Number:", num); // Will print in sorted order: 1, 3, 4
    }
}

fn main() -> void {
    var person: tuple<string, i32, f32> = tuple("Alice", 30, 65.5);

    var name: string = std::get<0>(person);
    var age: i32 = std::get<1>(person);
    var height: f32 = std::get<2>(person);

    print("Name:", name);
    print("Age:", age);
    print("Height:", height);

    // Tuple unpacking
    var (name2, age2, height2) = person;
    print("Unpacked - Name:", name2, "Age:", age2, "Height:", height2);
}
