example.main:
fn main() -> void {
    fn take_reference(param: reference(i32)) -> void {
        print("Reference value:", param);
    }

    var value: i32 = 42;
    take_reference(value);
}

fn main() -> void {
    fn take_move_reference(param: move_reference(i32)) -> void {
        print("Move reference value:", param);
    }

    var value: i32 = 42;
    take_move_reference(std::Memory::forward(value));
}

fn main() -> void {
    print("Hello, World!");
    print("Number:", 42);
    print("Multiple values:", 1, 2.5, "text", true);

    // Using custom endlines
    print("Line 1", std::endl("\n"));
    print("Line 2", std::endl("\r\n"));

    // Mixed types
    var name: string = "Kairo";
    var version: f32 = 1.0;
    print("Welcome to", name, "version", version);
}
