fn main() -> void {
    var file: *File = null;

    try {
        file = std::Legacy::new<File>("data.txt");
        // Process file
        file.read();
    } catch (error: std::Error::RuntimeError) {
        print("Error:", error.message());
    } finally {
        // This always executes, even if an exception occurs
        if (file != null) {
            file.close();
            delete file;
        }
        print("Cleanup completed");
    }
}

fn main() -> void {
    var int_val: i32 = 42;
    var float_val: f32 = int_val as f32;
    var const_val: const i32 = int_val as const i32;

    print("Int:", int_val);
    print("Float:", float_val);
    print("Const:", const_val);

    // Unsafe cast example
    var ptr: *i32 = &int_val;
    var void_ptr: *void = ptr as unsafe *void;
    print("Pointer cast successful");
}

fn main() -> void {
    // Simple lambda
    var add: fn(i32, i32) -> i32 = fn(a: i32, b: i32) -> i32 {
        return a + b;
    };

    print("Addition:", add(5, 3));

    // Lambda with capture
    var multiplier: i32 = 10;
    var multiply: fn(i32) -> i32 = fn(x: i32) -> i32 {
        return x * multiplier;
    };

    print("Multiplication:", multiply(4));

    // Using lambda with higher-order functions
    var numbers: vec<i32> = {1, 2, 3, 4, 5};
    var squared: vec<i32> = numbers.map(fn(x: i32) -> i32 {
        return x * x;
    });

    for num in squared {
        print("Squared:", num);
    }
}

fn main() -> void {
    fn safe_divide(a: i32, b: i32) -> i32? {
        if (b == 0) {
            return null; // Returns null for division by zero
        }
        return a / b;
    }

    fn risky_divide(a: i32, b: i32) -> i32? {
        if (b == 0) {
            return std::Error::RuntimeError("Division by zero"); // Returns error
        }
        return a / b;
    }

    // Safe usage
    var result1: i32? = safe_divide(10, 2);
    if (result1 ?) {
        print("Result:", result1 as i32);
    } else {
        print("Division failed");
    }

    // Handling null
    var result2: i32? = safe_divide(10, 0);
    if (result2 == null) {
        print("Got null result");
    }

    // Handling errors
    var result3: i32? = risky_divide(10, 0);
    if (std::Error::RuntimeError in result3) {
        var error: std::Error::RuntimeError = result3 as std::Error::RuntimeError;
        print("Got error:", error.message());
    }
}
