fn main() -> void {
    var num: i32 = 42;
    var float_val: f32 = num as f32;
    print(float_val);
}

fn main() -> void {
    var num: i32 = 42;
    var const_num: const i32 = num as const i32;
    print(const_num);
}

fn main() -> void {
    var ptr: *i32 = std::Legacy::new<i32>(42);
    var void_ptr: *void = ptr as unsafe *void;
    print("Unsafe cast performed");
}

fn main() -> void {
    fn counter() -> yield i32 {
        for i in std::range(0, 10) {
            yield i;
        }
    }
    var gen: Generator<i32> = counter();
    var value: i32 = std::next(gen);
    print(value);
}

fn main() -> void {
    print("Hello", std::endl("\n"), "World");
}

fn main() -> void {
    var name: string = "Kairo";
    var version: i32 = 1;
    var formatted: string = std::stringf("Welcome to %s version %d", name, version);
    print(formatted);
}

fn main() -> void {
    var error: std::Error::RuntimeError = std::Error::RuntimeError("Critical error");
    std::crash(error); // Program terminates here
}

fn main() -> void {
    var num: i32 = 42;
    var erased: *std::TypeErasure = std::erase_type(&num);
    print("Type erased");
}

fn main() -> void {
    var r: std::Range<i32> = std::range(10);
    for i in r {
        print(i);
    }
}

fn main() -> void {
    var r: std::Range<i32> = std::range(0, 10, 2);
    for i in r {
        print(i); // Prints 0, 2, 4, 6, 8
    }
}

fn main() -> void {
    var num: i32 = 42;
    var str: string = std::to_string(num);
    print(str);

    var flag: bool = true;
    var bool_str: string = std::to_string(flag);
    print(bool_str);
}

fn main() -> void {
    var wide_char: wchar_t = 'H';
    var narrow_char: char = std::char_to_cchar(wide_char);
    print(narrow_char);
}

fn main() -> void {
    var s_str: nstring = "Hello";
    var w_str: string = std::nstring_to_string(s_str);
    print(w_str);
}

fn main() -> void {
    var c_str: const char* = "Hello";
    var str: string = std::nptr_to_string(c_str, 5);
    print(str);
}

fn main() -> void {
    var w_str: string = "Hello";
    var s_str: nstring = std::string_to_nstring(w_str);
    print(s_str);
}

fn main() -> void {
    var w_str: const wchar_t* = L"Hello";
    var buffer: char[100];
    std::wptr_to_nptr(w_str, buffer, 100);
    print(buffer);
}

fn main() -> void {
    fn add(a: i32, b: i32) -> i32 {
        return a + b;
    }
    var func: std::Function<i32, i32, i32> = std::Function(add);
    var result: i32 = func(5, 3);
    print(result);
}

fn main() -> void {
    fn fibonacci() -> yield i32 {
        var a: i32 = 0;
        var b: i32 = 1;
        while (true) {
            yield a;
            var temp: i32 = a + b;
            a = b;
            b = temp;
        }
    }
    var gen: Generator<i32> = fibonacci();
    print(std::next(gen));
    print(std::next(gen));
}

fn main() -> void {
    var null_val: std::null_t = std::null_t();
    var ptr: *i32 = null;
    if (ptr == null) {
        print("Pointer is null");
    }
}

fn main() -> void {
    var num: i32 = 42;
    var erased: *std::TypeErasure = std::erase_type(&num);
    var type_info: libcxx::type_info = erased.type_info();
    var cloned: *std::TypeErasure = erased.clone();
    erased.destroy();
}

fn main() -> void {
    var r: std::Range<i32> = std::Range(0, 10, 2);

    for i in r {
        print(i);
    }

    if (5 in r) {
        print("5 is not in range");
    }

    if (4 in r) {
        print("4 is in range");
    }
}

fn main() -> void {
    fn divide(a: i32, b: i32) -> i32? {
        if (b == 0) {
            return std::Error::RuntimeError("Division by zero");
        }
        return a / b;
    }

    var result: i32? = divide(10, 2);
    if (result ?) {
        print("Result:", result as i32);
    } else {
        print("Error occurred");
    }
}
