fn main() -> void {
    class CustomError : std::Error::BaseError {
        message: string;

        fn CustomError(self, msg: string) {
            self.message = msg;
        }

        fn op as<T>(self) -> T {
            // Implementation depends on target type
            return self as T;
        }

        fn op panic(self) -> std::Panic::Frame {
            return std::Panic::Frame(self, __FILE__, __LINE__);
        }
    }

    var error: CustomError = CustomError("Custom error occurred");
    panic error;
}

fn main() -> void {
    fn process_pointer(ptr: *i32) -> i32 {
        if (ptr == null) {
            panic std::Error::NullValueError("Null pointer encountered");
        }
        return *ptr;
    }

    try {
        var result: i32 = process_pointer(null);
        print("Result:", result);
    } catch (error: std::Error::NullValueError) {
        print("Caught NullValueError:", error.message());
    }
}

fn main() -> void {
    fn divide(a: i32, b: i32) -> i32 {
        if (b == 0) {
            panic std::Error::RuntimeError("Division by zero");
        }
        return a / b;
    }

    try {
        var result: i32 = divide(10, 0);
        print("Result:", result);
    } catch (error: std::Error::RuntimeError) {
        print("Caught RuntimeError:", error.message());
    }
}

fn main() -> void {
    class StateMachine {
        state: string;

        fn StateMachine(self) {
            self.state = "initial";
        }

        fn start(self) -> void {
            if (self.state != "initial") {
                panic std::Error::StateMismatchError("Cannot start from state: " + self.state);
            }
            self.state = "running";
        }

        fn stop(self) -> void {
            if (self.state != "running") {
                panic std::Error::StateMismatchError("Cannot stop from state: " + self.state);
            }
            self.state = "stopped";
        }
    }

    var machine: StateMachine = StateMachine();
    machine.start();

    try {
        machine.start(); // This will throw StateMismatchError
    } catch (error: std::Error::StateMismatchError) {
        print("Caught StateMismatchError:", error.message());
    }
}

fn main() -> void {
    fn safe_cast<From, To>(value: From) -> To {
        // Pseudo-code for type checking
        if (!std::Meta::is_convertible<From, To>) {
            panic std::Error::TypeMismatchError("Cannot convert from " + 
                typeid(From).name() + " to " + typeid(To).name());
        }
        return value as To;
    }

    try {
        var result: string = safe_cast<i32, string>(42);
        print("Result:", result);
    } catch (error: std::Error::TypeMismatchError) {
        print("Caught TypeMismatchError:", error.message());
    }
}
