fn main() -> void {
    var obj: *BaseClass = new DerivedClass();
    var derived: *DerivedClass = obj as *DerivedClass;
    // obj must satisfy SupportsPointerCast<DerivedClass>
}

fn main() -> void {
    var num: i32 = 42;
    var str: string = num as string; // i32 must be Castable<i32, string>
}

fn main() -> void {
    var num: i32 = 42;
    var str: string = std::to_string(num); // i32 must be ConvertibleToString<i32>
    print(str);
}

fn main() -> void {
    var range: std::Range<i32> = std::range(0, 10);
    for i in range {
        print(i);
    }
}

fn main() -> void {
    var num: i32 = 42;
    print(num); // i32 must support SupportsOStream<i32>
}

fn main() -> void {
    class MyClass {}
    // MyClass satisfies ClassType<MyClass>
}

fn main() -> void {
    var value: const i32 = 42;
    // const i32 satisfies ConstType<const i32>
}

fn main() -> void {
    var value: i32 = 42;
    var ref: &i32 = &value; // i32 must be ReferenceableType<i32>
}

fn main() -> void {
    var value: i32 = 42;
    var rref: &&i32 = std::Memory::forward(value);
}

fn main() -> void {
    var value: i32 = 42;
    var lref: &i32 = &value;
}

fn main() -> void {
    var value: i32 = 42;
    var ref: &i32 = &value; // &i32 satisfies ReferenceType<&i32>
}

fn main() -> void {
    var vec: vec<i32> = vec<i32>();
    var moved: vec<i32> = std::Memory::forward(vec);
}

fn main() -> void {
    var a: i32 = 42;
    var b: i32 = 24;
    a = b; // Assignment must be noexcept
}

fn main() -> void {
    var original: vec<i32> = vec<i32>();
    var copy: vec<i32> = original; // vec<i32> must be CopyConstructible
}
