fn main() -> void {
    try {
        panic std::Error::RuntimeError("Test error");
    } catch (frame: std::Panic::Frame) {
        var context: *std::Panic::FrameContext = frame.get_context();
        print("Panic type:", context.type_name());
        print("Panic object:", context.object());
    }
}

fn main() -> void {
    fn divide(a: i32, b: i32) -> i32 {
        if (b == 0) {
            panic std::Error::RuntimeError("Division by zero");
        }
        return a / b;
    }

    try {
        var result: i32 = divide(10, 0);
        print("Result:", result);
    } catch (frame: std::Panic::Frame) {
        print("Panic occurred in file:", frame.file());
        print("At line:", frame.line());
        print("Reason:", frame.reason());
    }
}
