fn main() -> void {
    class MyError {
        message: string;

        fn MyError(self, msg: string) {
            self.message = msg;
        }

        fn op panic(self) -> std::Panic::Frame {
            return std::Panic::Frame(self, __FILE__, __LINE__);
        }
    }

    // MyError now satisfies Panicking<MyError>
    var error: MyError = MyError("Something went wrong");
    panic error;
}

fn main() -> void {
    class StaticError {
        static fn op panic() -> std::Panic::Frame {
            return std::Panic::Frame(StaticError(), __FILE__, __LINE__);
        }
    }

    // StaticError now satisfies PanickingStatic<StaticError>
    panic StaticError::op_panic();
}

fn main() -> void {
    class InstanceError {
        error_code: i32;

        fn InstanceError(self, code: i32) {
            self.error_code = code;
        }

        fn op panic(self) -> std::Panic::Frame {
            return std::Panic::Frame(self, __FILE__, __LINE__);
        }
    }

    // InstanceError now satisfies PanickingInstance<InstanceError>
    var error: InstanceError = InstanceError(404);
    panic error;
}
